/* [Overlay] Plugin
-------------------------------------------------------------*/
(function($){
	$.fn.overlay = function() {
		var ov = this;
		$('body').addClass('ov');
		ov.fadeIn();
		ov.css({
			left: $(window).width()/2 - ov.width()/2,
			//top: $(window).height()/2 - ov.height()/2
		});
		
		$('#overlay').css({
			display:'none', position:'fixed', 
			width: $(document).width() + 100,
			height: $(document).height() + 100,
			top: -100 + 'px',
			left: -100 + 'px'
		});
		
		$('#overlay').fadeIn('', function() {
			$('#overlay').bind('click', function() {
				$('body').removeClass('ov');
				$('#overlay').fadeOut();
				ov.fadeOut();
			});
		});
	};
})(jQuery);



